import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
   const [newExpenseForm,setNewExpenseForm]=useState(<div className="new-expense__actions">
  <button onClick>Add New Expense</button>
</div>);

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
    console.log(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    console.log(enteredDate);
    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    props.onSaveExpenseData(expenseData);
    setEnteredAmount("");
    setEnteredDate("");
    setEnteredTitle("");
  };
  let expenseFormState = 1;

  const cancelAddExpense = (event) => {
    event.preventDefault();
    console.log(expenseFormState);
    expenseFormState = 0;
    console.log(expenseFormState);
   
      console.log(expenseFormState);
      if (expenseFormState === 0)
      {
     setNewExpenseForm(<form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            type="text"
            value={enteredTitle}
            onChange={titleChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            value={enteredAmount}
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            value={enteredDate}
            onChange={dateChangeHandler}
          />
        </div>
      </div>

      <div className="new-expense__actions">
        <button onClick={cancelAddExpense}>Cancel</button>
        <button type="submit">Add Expense</button>
      </div>
    </form>)
      }
  };
//  return (
//         <div className="new-expense__actions">
//           <button>Add New Expense</button>
//         </div>
//       );
  return (
    newExpenseForm
  );
};
export default ExpenseForm;
