  expenseForm=
    <div className="new-expense__actions">
      <button onClick={showExpenseForm}>Add New Expense</button>
    </div>;

  expenseForm = <form onSubmit={submitHandler}>
  <div className="new-expense__controls">
    <div className="new-expense__control">
      <label>Title</label>
      <input
        type="text"
        value={enteredTitle}
        onChange={titleChangeHandler}
      />
    </div>
    <div className="new-expense__control">
      <label>Amount</label>
      <input
        type="number"
        min="0.01"
        step="0.01"
        value={enteredAmount}
        onChange={amountChangeHandler}
      />
    </div>
    <div className="new-expense__control">
      <label>Date</label>
      <input
        type="date"
        min="2019-01-01"
        max="2022-12-31"
        value={enteredDate}
        onChange={dateChangeHandler}
      />
    </div>
  </div>
  
  <div className="new-expense__actions">
    <button onClick={showAddExpense}>Cancel</button>
    <button type="submit">Add Expense</button>
  </div>
</form>

  return (
    expenseForm
  );
};
export default ExpenseForm;
