import React, {useState} from 'react';
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear]=useState('2021');
  let finalDiv = [];

  const filterYearChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
    
  };

  let filterArray = finalDiv.filter(expense => expense.props.date.getFullYear().toString() === selectedYear);

  for (let i = 0; i < props.expenses.length; i++) {
    filterArray = [
      ...filterArray,
      <ExpenseItem 
        title={props.expenses[i].title}
        amount={props.expenses[i].amount}
        date={props.expenses[i].date}
        key={props.expenses[i].id}
      />,
    ];
  }
  return (
    <div>
      {" "}
      <Card className="expenses">
        {" "}
        <ExpensesFilter selected={filteredYear} onSelectedYear={filterYearChangeHandler} /> {finalDiv}
      </Card>
    </div>
  );
};

export default Expenses;
